async function callMyPromise(){try{const o=await new Promise(function(o,n){setTimeout(()=>{resolve("ok")},2e3)});console.log(o),console.log("fin asynchrone")}catch(o){console.error(o)}}new Promise(function(o,n){setTimeout(()=>{n("ko")},2e3)}).then(function(o){console.log(o)}).then(function(){console.log("fin programme")}).catch(function(o){console.log(o)}),console.log("fin asynchrone"),callMyPromise(),console.log("fin sync");